// ContactUs.js

import React, { useState } from 'react';

const ContactUs = () => {
  const [faq, setFaq] = useState([
    { question: 'Pertanyaan 1', answer: 'Jawaban 1', isOpen: false },
    { question: 'Pertanyaan 2', answer: 'Jawaban 2', isOpen: false },
    { question: 'Pertanyaan 3', answer: 'Jawaban 3', isOpen: false },
    { question: 'Pertanyaan 4', answer: 'Jawaban 4', isOpen: false },
    { question: 'Pertanyaan 5', answer: 'Jawaban 5', isOpen: false },
  ]);

  const toggleFAQ = (index) => {
    setFaq((prevState) => {
      const updatedFaq = [...prevState];
      updatedFaq[index].isOpen = !updatedFaq[index].isOpen;
      return updatedFaq;
    });
  };

  return (
    <div>
      <h2 className="text-2xl font-bold mb-4">FAQ</h2>
      {faq.map((item, index) => (
        <div key={index} className="mb-4">
          <div className="flex items-center">
            <button
              className="mr-2 text-blue-500"
              onClick={() => toggleFAQ(index)}
            >
              {item.isOpen ? '-' : '+'}
            </button>
            <p className="font-semibold">{item.question}</p>
          </div>
          {item.isOpen && <p>{item.answer}</p>}
        </div>
      ))}

      <h2 className="text-2xl font-bold mt-8 mb-4">Our Contact</h2>
      {/* Tampilkan kontak pemilik web di sini */}
    </div>
  );
};

export default ContactUs;
